  if v.faction == "Combine" then
   Clockwork.directory:AddCode("Civil Protection", [[
    <div class="auraInfoTitle">]]..string.upper(v.command)..[[</div>
    <div class="auraInfoText">]]..v.phrase..[[</div>
   ]], true);
 end;
end;
end;